SELECT GROUP_CONCAT(tbody Separator '$$') 表格拼接
from(
SELECT p.`projectId` pid,
p.`title` ,
p.`onlineTime` ,
p.`abstract`,
COUNT(if(pie.`type`  ='bp_viewed',pie.`eventId`,null)) bp查看,
COUNT(if(pie.`type`  ='view',pie.`eventId`,null)) 项目查看,
COUNT(if(pie.`type`  ='exposed',pie.`eventId`,null)) 曝光,
group_concat(
    distinct(
        concat_ws('@@', p.`projectId`,p.`title`,p.`financingRound`,p.`abstract`,p.`highlights`)
    ) 
    SEPARATOR '$$'
) tbody
from `project` p
left join `project_related_investor` pri on
(pri.`projectId` = p.`projectId`  and pri.type  in('rejected'))
left join `project_investor_event` pie on pie.`projectId` =p.`projectId`
#关联项目可见性（后面筛选状态）
left join `project_visiblity`pv on pv.projectId = p.`projectId`
where
DATE_FORMAT(p.`onlinetime`, '%Y%m%d') 
BETWEEN DATE_FORMAT(from_unixtime(unix_timestamp() - 86400*8), '%Y%m%d') 
and DATE_FORMAT(from_unixtime(unix_timestamp() - 86400*1), '%Y%m%d')
and p.status2 = 5000 #在线
and pv.exposeStatus !=100 #非暂停曝光
and p.`level` = 2 #精选
and pri.`id` is null #没有曝光限制
and pv.visibleMode = 2 #可见性规则是黑名单的项目（默认是黑名单模式） 
GROUP BY p.`projectId`
order by COUNT(pie.`eventId`) desc
LIMIT 10) temp



#Top浏览项目
SELECT GROUP_CONCAT(tbody Separator '$$') 表格拼接
from (select
p.`projectId` pid,
p.`title` 项目名,
#p.`onlineTime` 上线时间,
#COUNT(distinct(m.`meetingId`)) 约会数,
p.`financingRound` 轮次,
p.`abstract` 项目简介,
p.`highlights` 项目亮点,
group_concat(
    distinct(
        concat_ws('@@', p.`projectId`,p.`title`,p.`financingRound`,p.`abstract`,p.`highlights`)
    ) 
    SEPARATOR '$$'
) tbody
FROM `meeting` m
LEFT JOIN `project` p
on m.`projectId` =p.`projectId` 
left join `project_related_investor` pri on
(pri.`projectId` = p.`projectId` and pri.type in('rejected'))
left join `project_investor_event` pie on pie.`projectId` =p.`projectId`
LEFT JOIN `project_visiblity` pv on pv.`projectId` = p.`projectId`
WHERE 
DATE_FORMAT(m.`creationTime`, '%Y%m%d') 
BETWEEN DATE_FORMAT(from_unixtime(unix_timestamp() - 86400*8), '%Y%m%d') 
and DATE_FORMAT(from_unixtime(unix_timestamp() - 86400*1), '%Y%m%d')
and m.`status` ='online'
and p.`status2` ='5000'
and pri.`id` is null #没有曝光限制
and pv.visibleMode = 2 #可见性规则是黑名单的项目（默认是黑名单模式）
GROUP BY p.`title` 
ORDER BY COUNT(distinct(m.`meetingId`)) desc,p.`onlineTime` asc
limit 10) temp